	document.body,
	NodeFilter.SHOW_ELEMENT,
	{ acceptNode: function(node) { return NodeFilter.FILTER_ACCEPT; } },
	false
	);
var nodeList = new Array();
var currentNode;

while (currentNode = nodeIterator.nextNode()) {
	nodeList.push(currentNode);
}

