function newHighlite(obj) {            
	oRcts = obj.getClientRects();
	iLength = oRcts.length;
	for (i = 0;i < iLength; i++) {	
		alert("Line number " + (i + 1) + " is " + (oRcts(i).right - oRcts(i).left) + " pixels wide.");
	}
}
</script>

